import React, { useState, useEffect, useRef } from 'react';
import CardList from './components/CardList';
import FilterDropdown from './components/FilterDropdown';
import SearchBar from './components/SearchBar';
import Tabs from './components/Tabs';
import mockData from './data/mockData';

import './App.css';

const App = () => {
  const [cards, setCards] = useState([]);
  const [filteredCards, setFilteredCards] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('');
  const [activeTab, setActiveTab] = useState('Your');
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const containerRef = useRef(null);

  useEffect(() => {
    // Simulating API call to fetch initial data
    // Replace with your actual API call
    fetchCards();
  }, []);

  useEffect(() => {
    // Filter cards based on search term and card type
    const filtered = cards.filter((card) => {
      const nameMatch = card.name.toLowerCase().includes(searchTerm.toLowerCase());
      const typeMatch = filterType === '' || card.card_type === filterType;
      return nameMatch && typeMatch;
    });
    setFilteredCards(filtered);
  }, [cards, searchTerm, filterType]);

  useEffect(() => {
    const observer = new IntersectionObserver(handleObserver, { threshold: 1 });
    if (containerRef.current) {
      observer.observe(containerRef.current);
    }
    return () => {
      if (containerRef.current) {
        observer.unobserve(containerRef.current);
      }
    };
  }, []);

  const handleObserver = (entries) => {
    const target = entries[0];
    if (target.isIntersecting && !loading && hasMore) {
      setPage((prevPage) => prevPage + 1);
    }
  };

  const fetchCards = () => {
    setLoading(true);
    // Simulating API call to fetch data
    // Replace with your actual API call
    const start = (page - 1) * 10;
    const end = start + 10;
    const newData = mockData.data.slice(start, end);

    setTimeout(() => {
      setCards((prevCards) => [...prevCards, ...newData]);
      setLoading(false);
      if (end >= mockData.total) {
        setHasMore(false);
      }
    }, 1000);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterType(event.target.value);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setPage(1); // Reset page when changing tabs
  };

  return (
    <div className="App">
      <Tabs activeTab={activeTab} onTabChange={handleTabChange} />
      <FilterDropdown filterType={filterType} onChange={handleFilterChange} />
      <SearchBar value={searchTerm} onChange={handleSearchChange} />
      <CardList cards={filteredCards} />
      {loading && <div>Loading...</div>}
      {!loading && hasMore && (
        <div ref={containerRef} style={{ height: '10px' }}></div>
      )}
    </div>
  );
};

export default App;


