import React, { useState, useEffect } from 'react';
import CardList from './components/CardList';
import FilterDropdown from './components/FilterDropdown';
import SearchBar from './components/SearchBar';
import Tabs from './components/Tabs';
import mockData from './Data/mockData';

const App = () => {
  const [cards, setCards] = useState([]);
  const [filteredCards, setFilteredCards] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('');
  const [activeTab, setActiveTab] = useState('Your');

  useEffect(() => {
    // Simulating API call to fetch data
    // Replace with your actual API call
    setCards(mockData.data);
    setFilteredCards(mockData.data);
  }, []);

  useEffect(() => {
    // Filter cards based on search term and card type
    const filtered = cards.filter((card) => {
      const nameMatch = card.name.toLowerCase().includes(searchTerm.toLowerCase());
      const typeMatch = filterType === '' || card.card_type === filterType;
      return nameMatch && typeMatch;
    });
    setFilteredCards(filtered);
  }, [cards, searchTerm, filterType]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterType(event.target.value);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);

    // Filter cards based on owner_id when tab is clicked
    const filtered = cards.filter((card) => {
      if (tab === 'Your') {
        // Filter cards belonging to the user (owner_id 1)
        return card.owner_id === 1;
      } else if (tab === 'all') {
        // Show all cards
        return true;
      } else if (tab === 'blocked') {
        // Filter cards with status 'blocked'
        return card.status === 'blocked';
      }
      return false;
    });

    setFilteredCards(filtered);
  };

  return (
    <div className="App">
      <Tabs activeTab={activeTab} onTabChange={handleTabChange} />
      <FilterDropdown filterType={filterType} onChange={handleFilterChange} />
      <SearchBar value={searchTerm} onChange={handleSearchChange} />
      <CardList cards={filteredCards} />
    </div>
  );
};

export default App;
